import React, { useState, useEffect } from "react";
import Layout from "../../components/Layout";
import { Container, Form, Row, Col, Button } from "react-bootstrap";
import Input from "../../components/UI/Input";
import { login } from "../../actions";
import { useDispatch, useSelector } from "react-redux";
import { Redirect } from "react-router-dom";
import loginImg from "../../icons/login.png";
import "./singin.css";
/**
 * @author
 * @function Signin
 **/

const Signin = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const auth = useSelector((state) => state.auth);

  const dispatch = useDispatch();

  const userLogin = (e) => {
    e.preventDefault();

    const user = {
      email,
      password,
    };

    dispatch(login(user));
  };

  if (auth.authenticate) {
    return <Redirect to={`/`} />;
  }

  return (
    <Layout>
      <Container>
        <Row style={{ marginTop: "80px" }} className="signinForm">
          <div class="login-form boxes">
            <div class="login-form">
              <img src={loginImg} alt="login" className="img-fluid" />
              <Col md={{ span: 6, offset: 3 }}>
                <Form onSubmit={userLogin}>
                  <Input
                    label="Email"
                    placeholder="Email"
                    value={email}
                    type="email"
                    onChange={(e) => setEmail(e.target.value)}
                  />

                  <Input
                    label="Password"
                    placeholder="Password"
                    value={password}
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                  />
                  <Button
                    variant="primary"
                    type="submit"
                    style={{ marginLeft: "35%" }}
                  >
                    Submit
                  </Button>
                  <div class="modal-footer b-0 row d-flex jusstify-content-end">
                    <div class="mf-link text-center">
                      <i class="ti-user"></i>Hav'nt an account already?
                      <a href="signup" class="theme-cl">
                        {" "}
                        Signup
                      </a>
                    </div>
                  </div>
                </Form>
              </Col>
            </div>
          </div>
        </Row>
      </Container>
    </Layout>
  );
};

export default Signin;
